import React from 'react';
import s from "./MyPosts.module.css";
import Post from './post/Post';
import { updateNewPostTextActionCreater , addPostActionCreater } from '../../../redux/profile-reducer';

const MyPosts = (props) => {

    let postsElements = props.postsData.map(p  => <Post message={p.message} likequantity={p.likequantity}/>)

    let newPostElement = React.createRef(); 

    let addPost = () => {
        props.dispatch(addPostActionCreater());
    }
    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.dispatch(updateNewPostTextActionCreater(text));
    }

    return (
        <div className={s.postsBlock}>
            <h3>My post</h3>
            <textarea onChange={ onPostChange } ref={ newPostElement } value={ props.newPostState }/>
            <button onClick={ addPost }>Add post</button>
            <div className={s.posts}>
              {postsElements}
              </div>
        </div>
    );
}

export default MyPosts;
