import React from 'react'
import s from './Paginator.module.css'

const Paginator = ({totalItemsCount , pageSize , }) => {

    let pagesCount = Math.ceil(totalItemsCount / props.pageSize)

    let pages = [];

    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }

    let portionsCount = Math.ceil(props.pagesCount/portionsSize)
    let [portionNumber , setPortionNumber] = useState(1)
    let leftPortionPageNumber = (portionNumber - 1) * portionsSize + 1
    let rightPortionPageNumber = portionNumber * portionsSize

    return  <div className={s.currentWrap}>
            {pages
            .filter(p => p >= leftPortionPageNumber && p <= rightPortionPageNumber)
            .map(p => {
                return <span className={props.currentPage === p && s.selectedPage}
                    onClick={(e) => { props.onPageChange(p) }}>
                    {p}</span>
            })}
        </div>
}

export default Paginator
