import React from 'react';
import MyPosts from './MyPosts';
import { updateNewPostTextActionCreater , addPostActionCreater } from '../../../redux/profile-reducer';

const MyPostsContainer = (props) => {

    let state = props.getState();

    let addPost = () => {
        props.dispatch(addPostActionCreater());
    }
    let onPostChange = (text) => {
        let action = updateNewPostTextActionCreater(text);
        props.dispatch(action);
}

    return (<MyPosts updateNewPostText = { onPostChange } addPost = { addPost } newPostState={state.ProfilePage.newPostState} posts = {state.ProfilePage.posts}/>);
}

export default MyPostsContainer;
