import React from 'react';
import s from "./MyPosts.module.css";
import Post from './post/Post';
import { updateNewPostTextActionCreater, addPostActionCreater } from '../../../redux/pro';




const MyPosts = (props) => {

    let postsElements = props.postsData.map(p  => <Post message={p.message} likequantity={p.likequantity}/>)

    let newPostElement = React.createRef(); 

    let addPost = () => {
        props.dispatch(addPostActionCreater());
    }
    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.dispatch(updateNewPostTextActionCreator(text));
    }

    return (
        <div className={s.postsBlock}>
            <h3>My post</h3>
            <div><textarea onChange={onPostChange} ref={newPostElement} value={props.newPostState}/></div>
            <button onClick={addPost}>Add post</button>
            <div className={s.posts}>
              {postsElements}
              </div>
        </div>
    );
}

export default MyPosts;
