const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';

let initialState = {
    postsData: [
        { id: 1, message: 'Hi', likequantity: 23 },
        { id: 2, message: 'Hello', likequantity: 23 },
        { id: 3, message: 'you', likequantity: 23 },
        { id: 3, message: 'Yo', likequantity: 23 },
        { id: 5, message: 'Good bay', likequantity: 23 },
        { id: 6, message: 'Lose', likequantity: 23 }
    ],
    newPostState: ''
};

const profileReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_POST:
            let newPost = {
                id: 8,
                message: state.newPostState,
                likequantity: 0
            };
            let stateCopy = {...state};
            stateCopy.posts = [...state.posts];

            state.newPostState = '';
            return state;
        case UPDATE_NEW_POST_TEXT:
            state.newPostState = action.newText;
            return  state;
        default:
            return state;
    }
}

export const addPostActionCreater = () => ({type: ADD_POST});
export const updateNewPostTextActionCreater = (text) => ({type: UPDATE_NEW_POST_TEXT, newText: text});

export default profileReducer;
