const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET_USERS'

let initialState = {
    users: [
        { id: 1, photo: '' ,fullName: 'Alex', followed: false, status: 'hello', location: {city: 'Samara' , country: 'Russia'}},
        { id: 2, fullName: 'Matvey', followed: false, status: 'hello', location: {city: 'Samara' , country: 'Russia'}},
        { id: 3, fullName: 'Artem', followed: false, status: 'hello', location: {city: 'Samara' , country: 'Russia'}},
        { id: 4, fullName: 'Egor', followed: false, status: 'hello', location: {city: 'Samara' , country: 'Russia'}},
    ]
}

const usersReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW: 
            return {
                ...state,
                users: state.users.map(u => {
                    if(u.id === action.userId){
                        return {...u, followed: true}
                    }
                    return u
                })
            }
        case UNFOLLOW: 
            return {
                ...state,
                users: state.users.map(u => {
                    if(u.id === action.userId){
                        return {...u, followed: false}
                    }
                    return u
                })
            }
        case SET_USERS:
            return { ...state , users: [...state.users , action.users]}
        default:
            return state;
    }
}

export const followAC = (userId) => ({type: FOLLOW , userId})
export const unfollowAC = (userId) => ({type: UNFOLLOW , userId})
export const setUsersAC = (users) => ({type: SET_USERS , users})


export default usersReducer;
