import React from 'react';
import s from "./../Dialogs.module.css";

const Message = (props) => {

    let className = s.author;

    if(props.author === 'me'){
        className += 'me';
    }else{
        className += 'other';
    }

    return (
        <div className={`${s.message} ${className}`}>{props.message}</div>
    );
}

export default Message;
