import React from 'react';
import s from "./../Dialogs.module.css";

const Message = (props) => {

    var m;

    if(props.author == 'me'){
        m = 's.author_me';
    }else{
        m = s.author_other;
    }

    return (
        <div className={`${s.message} ${m}`}>{props.message}</div>
    );
}

export default Message;
