let store = {
    _state: {
        profilePage: {
            postsData: [
                { id: 1, message: 'Hi', likequantity: 23 },
                { id: 2, message: 'Hello', likequantity: 23 },
                { id: 3, message: 'you', likequantity: 23 },
                { id: 3, message: 'Yo', likequantity: 23 },
                { id: 5, message: 'Good bay', likequantity: 23 },
                { id: 6, message: 'Lose', likequantity: 23 }
            ],
            newPostState: 'text'
        },
        dialogsPage: {
            dialogs: [
                { id: 1, name: 'Alex' },
                { id: 2, name: 'Matvey' },
                { id: 3, name: 'Victor' },
                { id: 4, name: 'Sasha' },
                { id: 5, name: 'Artem' },
                { id: 6, name: 'Pasha' }
            ],
            messages: [
                { id: 1, message: 'Hi', author: 'me' },
                { id: 2, message: 'Hello', author: 'other' },
                { id: 3, message: 'How are you', author: 'me' },
                { id: 3, message: 'Good', author: 'other' },
                { id: 5, message: 'And you', author: 'other' },
                { id: 6, message: 'Good', author: 'me' }
            ],
            newMessageState: ''
        }
    },
    getState() {
        return this._state;
    },
    subscribe(observer) {
        this._callSubscriber = observer;
    },
    _callSubscriber() { },
    dispatch(action) {
        if (action.type === 'ADD-POST') {
            let newPost = {
                id: 8,
                message: this._state.profilePage.newPostState,
                likequantity: 0
            };
            this._state.profilePage.postsData.unshift(newPost);
            this._state.profilePage.newPostState = '';
            this._callSubscriber(this._state);
        } else if (action.type === 'UPDATE-NEW-POST-TEXT') {
            this._state.profilePage.newPostState = action.newText;
            this._callSubscriber(this._state);
        } else if (action.type === 'ADD-MESSAGE') {

            let newMessage = {
                id: 6,
                message: this._state.dialogsPage.newMessageState,
                author: 'me'
            };
            this._state.dialogsPage.messages.push(newMessage);
            this._state.dialogsPage.newMessageState = '';
            this._callSubscriber(this._state);
        } else if (action.type === 'UPDATE-NEW-MESSAGE-TEXT') {
            this._state.dialogsPage.newMessageState = action.newText;
            this._callSubscriber(this._state);
        }
    }
}

export const addMessageActionCreator = () => {
    return {
        type: ADD_MESSAGE
    }
}
export const addPostActionCreater = () => ({type: ADD_POST})
export const updateNewPostTextActionCreater = (text) => ({type: UPDATE_NEW_POST_TEXT, newText: text})
export const updateNewMessageTextActionCreater = (text) => ({type: UPDATE_NEW_MESSAGE_TEXT, newText: text})

export default store;
