import React from 'react';
import Dialogs from './Dialogs';
import { addMessageActionCreator , updateNewMessageTextActionCreater} from '../../redux/dialogs-reduser';

const DialogsContainer = (props) => {

    let state = props.store.getState().dialogsPage;

    let addMessage = () => {
        props.dispatch(addMessageActionCreator());
    }
    let onPostChange = (text) => {
        props.store.dispatch(updateNewMessageTextActionCreater(text));
    }

    return (
        <Dialogs 
        addMessage = { addMessage }
        updateNewMessageBody = { onPostChange }
        dialogsPage = { state }
        newMessageState = { state.newMessageState }
        />
    );
}

export default DialogsContainer;
