import React from 'react';
import Dialogs from './Dialogs';
import StoreContext from '../../StoreContext';
import { addMessageActionCreator , updateNewMessageTextActionCreater} from '../../redux/dialogs-reduser';

const DialogsContainer = (props) => {



    return  <StoreContext.Consumer>
        { 
         (store) => {
            let state = store.getState().dialogsPage;

            let addMessage = () => {
                store.dispatch(addMessageActionCreator());
            }
            let onPostChange = (text) => {
                store.dispatch(updateNewMessageTextActionCreater(text));
            }
                return <Dialogs 
        addMessage = { addMessage }
        updateNewMessageBody = { onPostChange }
        dialogsPage = { state }
        newMessageState = { state.newMessageState }
        />  
            }
        }

        </StoreContext.Consumer>
}

let mapStateToProps = (state) => {
    return{
        dialogsPage : state.dialogsPage
    }
}

let mapDispatchToProps = () => {
    return{

    }
}

const SuperDialogContainer = connect(mapStateToProps , mapDispatchToProps)(Dialogs);


export default DialogsContainer;
