import React from 'react';
import s from "./Dialogs.module.css";
import DialogItem from './DilogItem/DialogItems';
import Message from './Message/Message';
import { addMessageActionCreator, updateNewMessageTextActionCreater } from '../../redux/dialogs-reduser';
import { Redirect } from 'react-router-dom';

const Dialogs = (props) => {

    let state = props.dialogsPage;

    let dialogsElements = state.dialogs.map(d => <DialogItem name={d.name} id={d.id} />);
    let messageElements = state.messages.map(m => <Message message={m.message} author={m.author} />);

    let newMessage = React.createRef();

    let addMessage = () => {
        props.addMessage();
    }
    let onPostChange = () => {
        let body = newMessage.current.value;
        props.updateNewMessageBody(body);
    }

    if (!props.isAuth) return <Redirect to={"/login"} />

    return (
        <div className={s.dialogs}>
            <div className={s.dialogs_items}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                {messageElements}
                <AddMessageForm/>
            </div>
        </div>
    )
}

const AddMessageForm = (props) => {
    return <form onSubmit={props.ha}>
    <textarea onChange={onPostChange} ref={newMessage} value={props.newMessageState}></textarea>
            <button onClick={addMessage}>send</button>
            </form>
}

export default Dialogs;
