import React from 'react'
import {Field , reduxForm} from 'redux-form'
import { Input } from '../common/FormsControl/FormsControls'
import {required , maxLengthCreator } from '../../utils/validators/validators'
import { connect } from 'react-redux'
import {login} from '../../redux/auth-reducer'

const maxLength = maxLengthCreator(20)
const Login = () => {
    const onSubmit = (formData) =>{
        props.login(formData.email , formData.password , formData.rememberMe)
    }
    return <div>
        <h1>Login</h1>
        <LoginReduxForm onSubmit={onSubmit}/>
    </div>
}

const LoginForm = (props) => {
    return <form onSubmit={props.handelSubmit}>
            <div><Field placeholder={"Email"} component={Input} name={"email"} validate={[required , maxLength]}/></div>
            <div><Field placeholder={"password"} component={Input} name={"password"} validate={[required , maxLength]}/></div>
            <div><Field type={"checkbox"} component={"input"} name={"rememberMe"}/>remember Me</div>
            <div><input type={"button"} value={"login"}/></div>
        </form>
}
const LoginReduxForm = reduxForm({form: 'login'})(LoginForm)



export default connect(null , {login})(Login)
