const ProfileStatusWithHooks = (props) => {
    return <>
        <div>
            {true &&
                <div>
                    <span onDoubleClick={activateEditMode}>{props.status || "no status"}</span>
                </div>
            }{false &&
                <div>
                    <input onChange={this.onStatusChange}
                     autoFocus={true}
                     onBlur={deactivateEditMode}
                     value={""} />
                </div>
            }
        </div>
    </>
}

export default ProfileStatusWithHooks
