import React from 'react';
import s from "./../Dialogs.module.css";

const Message = (props) => {

   let author = '';

    if(props.author == 'me'){
        author += '_me';
    }else{
        author += '_other';
    }

    return (
    <div className={`${s.message} ${s = '.' + author}`}>{props.message} </div>
    );
}

export default Message;
