import React from 'react';
import s from './Users.module.css';
import * as axios from 'axios';
import userPhoto from '../../assets/img/1521018917.png';

class Users extends React.Component {

    componentDidMount() {
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(response => {
                this.props.setUsers(response.data.items);

            })
    }

    onPageChange = (pageNumber) => {
        this.props.setCurrentPage(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
        .then(response => {
            this.props.setUsers(response.data.items)
        })
    }

    render() {

        let pagesCount = Math.ceil( this.props.totalUsersCount / this.props.pageSize)

        let pages = [];

        for(let i = 1  ; i <= pagesCount ; i++) {
            pages.push(i);
        }

        return <div>
            <div>
                {pages.map(p => {
                return <span className={this.props.currentPage === p && s.selectedPage} onClick={(e) => {this.onPageChange(p)}}>{p}</span>
                })}
            </div>
            {
                this.props.users.map(u => <div key={u.id} className={s.wrap}>
                    <span>
                        <div>
                            <img className={s.avatar} src={u.photos.small != null ? u.photos.small : userPhoto} alt="" />
                        </div>
                        <div>
                            {u.followed ? <button className={s.unfollow} onClick={() => { this.props.unfollow(u.id) }} >unfollow</button> : <button className={s.follow} onClick={() => { this.props.follow(u.id) }}>follow</button>}
                        </div>
                    </span>
                    <span>
                        <span>
                            <div className={s.name}>{u.name}</div>
                            <div>{u.status}</div>
                        </span>
                        <span>
                            {/* <div>{u.location.country}</div> */}
                            {/* <div>{u.location.city}</div> */}
                        </span>
                    </span>
                </div>)
            }
        </div>
    }
}

export default Users;
