import React from 'react'
import s from './Users.module.css'
import userPhoto from '../../assets/img/1521018917.png'
import { NavLink } from 'react-router-dom'

let Users = (props) => {

    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize)

    let pages = [];

    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }
    return <div>
        <div className={s.cur}>
            {pages.map(p => {
                return <span className={props.currentPage === p && s.selectedPage}
                    onClick={(e) => { props.onPageChange(p) }}>
                    {p}</span>
            })}
        </div>
        {
            props.users.map(u => <div key={u.id} className={s.wrap}>
                <span>
                    <div>
                        <NavLink to={'/profile/' + u.id}>
                            <img className={s.avatar} src={u.photos.small != null ? u.photos.small : userPhoto} alt="" />
                        </NavLink>
                    </div>
                    <div>
                        {u.followed ?
                            <button disabled={props.followingInProgress.some(id => id === u.id)} className={s.unfollow} onClick={() => {props.unfollow(u.id)}} >unfollow</button> :
                            <button disabled={props.followingInProgress.some(id => id === u.id)} className={s.follow} onClick={() => {props.follow(u.id)}}>follow</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div className={s.name}>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        {/* <div>{u.location.country}</div> */}
                        {/* <div>{u.location.city}</div> */}
                    </span>
                </span>
            </div>)
        }
    </div>
}

export default Users
