const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT';
const ADD_MESSAGE = 'ADD-MESSAGE';

let initialState = {
    dialogs: [
        { id: 1, name: 'Alex' },
        { id: 2, name: 'Matvey' },
        { id: 3, name: 'Victor' },
        { id: 4, name: 'Sasha' },
        { id: 5, name: 'Artem' },
        { id: 6, name: 'Pasha' }
    ],
    messages: [
        { id: 1, message: 'Hi', author: 'me' },
        { id: 2, message: 'Hello', author: 'other' },
        { id: 3, message: 'How are you', author: 'me' },
        { id: 3, message: 'Good', author: 'other' },
        { id: 5, message: 'And you', author: 'other' },
        { id: 6, message: 'Good', author: 'me' }
    ],
    newMessageState: ''
};

const dialogsReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_MESSAGE:
            let newMessage = {
                id: 6,
                message: state.newMessageState,
                author: 'me'
            };
            let stateCopy = {...state}
            state.messages.push(newMessage);
            state.newMessageState = '';
            return  state;
        case UPDATE_NEW_MESSAGE_TEXT:
            state.newMessageState = action.newText;
            return  state;
        default:
            return state;
    }
}

export const updateNewMessageTextActionCreater = (text) => ({type: UPDATE_NEW_MESSAGE_TEXT, newText: text})
export const addMessageActionCreator = () => ({type: ADD_MESSAGE})

export default dialogsReducer;
