import React from 'react'
import s from "./ProfileInfo.module.css"
import Preloader from '../../common/preloader/preloader'
import HeaderBg from '../../../assets/img/Header.png'
import avatar from '../../../assets/img/1521018917.png'
import ProfileStatus from './ProfileStatus'
import ProfileStatusWithHooks from './ProfileStatusWithHooks'

const ProfileInfo = (props) => {
    if (!props.profile) {
        return <Preloader className={s.preloader} />
    }
    let largeAvatar = props.profile.photos.large
    if (largeAvatar === null) {
        largeAvatar = avatar
    }
    return (
        <div>
            <div>
                <img className={s.headerBg} src={HeaderBg} />
            </div>
            <div className={s.descriptionBlock}>
                <img src={largeAvatar} />
                <div className={s.name}>
                    {props.profile.fullName}
                </div>

            </div>
        </div>
    );
}

const ProfileData = ({profile}) => {
    <div className={s.description}>
                    <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus} />
                    <div>
                        <b>Looking for a job:</b> {props.profile.lookingForAJob ? "yes" : "no"}
                    </div>
                    {props.profile.lookingForAJob &&
                        <div>
                            <b>My professional skills: </b>{props.profile.lookingForAJobDescription}
                        </div>}
                    <div>
                        <b>About me:</b> {props.profile.aboutMe}
                    </div>
                    <div className={s.contacts}>
                        <b>Contacts: </b> {Object.keys(props.profile.contacts).map(k => {
                            return <Contact contactTitle={k} value={props.profile.contacts[k]}/>
                        })}
                    </div>
                </div>
}

const Contact = ({ contactTitle, value }) => {
    return (
        <div><b>{contactTitle}:</b>{value}</div>
    )
}

export default ProfileInfo;
